var Lib = require('../../lib');

module.exports = {
  hasLines: function hasLines(trace) {
    return trace.visible && trace.mode && trace.mode.indexOf('lines') !== -1;
  },
  hasMarkers: function hasMarkers(trace) {
    return trace.visible && (trace.mode && trace.mode.indexOf('markers') !== -1 || // until splom implements 'mode'
    trace.type === 'splom');
  },
  hasText: function hasText(trace) {
    return trace.visible && trace.mode && trace.mode.indexOf('text') !== -1;
  },
  isBubble: function isBubble(trace) {
    return Lib.isPlainObject(trace.marker) && Lib.isArrayOrTypedArray(trace.marker.size);
  }
